mapper.select:
select * from t_member
where id = (
	select memberid from t_ticket
	where piid = #{piid}
)

select * from t_cert
where id in (
	select certid
	from t_account_type_cert
	where accttype = #{accttype}
)

mapper.insert:
insert into t_ticket(
	memberid, status, pstep, piid
) values(
	#{memberid}, #{status}, #{pstep}, #{piid}
)

mapper.update:
update t_member
set accttype = #{accttype}
where id = #{id}

update t_ticket
set pstep = #{pstep}
where id = #{id}

update t_ticket
set pstep = #{pstep}, authcode = #{authcode}
where id = #{id}

update t_member
set realname = #{realname},
	cardnum = #{cardnum},
	tel = #{tel}
where id = #{id}
